package core_java;

public class StringMethod_compareToIgnoreCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//compareToIgnoreCase() ==> it ignores the UpperCase & lowerCase and returns difference between two stings
		
		String s1="D";//97
		String s2="B";//65     97-65=32
				
		System.out.println(s1.compareToIgnoreCase(s2));
	}

}


